import '../css/card.css';
import sunset from '../assets/sunset.gif';
import sunrise from '../assets/sunrise.gif';
import hot from '../assets/hot.gif';
import cold from '../assets/cold.gif';
import cloudy from '../assets/cloudy.gif';
import humidity from '../assets/humidity.png';
import pressure from '../assets/pressure.png';
import wind from '../assets/wind.gif';
import axios from 'axios';
import moment from 'moment';
import React, { useState } from 'react';

function Card() {
    const [defaultCity, setDefaultCity] = useState('nellore');
    const [timeGap, setTimeGap] = useState('');
    const [data, setData] = useState({
        city: '',
        temp: 0,
        temp_max: 0,
        temp_min: 0,
        humidity: 0,
        sunrise: 0,
        sunset: 0,
        country: '',
    });
    const [showWeather, setShowWeather] = useState(false); // State to control weather visibility

    const getData = (city) => {
        axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=d8596b1261b43be39522177d29112a96`)
            .then((response) => {
                setData({
                    desc: response.data.weather[0].main,
                    icon: response.data.weather[0].icon,
                    city: response.data.name,
                    temp: response.data.main.temp,
                    temp_max: response.data.main.temp_max,
                    temp_min: response.data.main.temp_min,
                    humidity: response.data.main.humidity,
                    pressure: response.data.main.pressure,
                    sunrise: response.data.sys.sunrise,
                    sunset: response.data.sys.sunset,
                    country: response.data.sys.country,
                    windSpeed: response.data.wind.speed,
                });
                setShowWeather(true); // Show weather information
            });
    };

    const handleOnChange = (e) => {
        clearTimeout(timeGap);
        const timeout = setTimeout(() => {
            getData(e.target.value);
        }, 500);
        setTimeGap(timeout);
    };

    const handleSearchClick = () => {
        getData(defaultCity);
    };

    return (
        <div>
            <div className="area">
                <ul className="circles">
                    {/* List items for your animated circles */}
                </ul>
            </div>
            <div className="container">
                <div className="card">
                    <div className="inputData">
                        <input
                            type="text"
                            className="inputField"
                            placeholder="Enter City Name"
                            onChange={handleOnChange}
                        />
                        <button className="btn" onClick={handleSearchClick} type="submit">
                            Search
                        </button>
                    </div>

                    {showWeather && (
                        <>
                            <div className="city">
                                <h3>
                                    <i className="fa-sharp fa-solid fa-location-dot"></i> {data.city} {data.country}
                                </h3>
                            </div>
                            <div className="info">
                                <h2>{data.temp} °C</h2>
                            </div>
                            <div className="boxes">
                                <div className="box">
                                    <h4>Max Temp</h4>
                                    <div className="border">
                                        <img src={hot} className="img" alt="max_temp" />
                                    </div>
                                    <h3>{data.temp_max} °C</h3>
                                </div>
                                <div className="box">
                                    <h4>Min Temp</h4>
                                    <div className="border">
                                        <img src={cold} className="img" alt="min_temp" />
                                    </div>
                                    <h3>{data.temp_min} °C</h3>
                                </div>
                                <div className="box">
                                    <h3>Wind Speed</h3>
                                    <div className="border">
                                        <img src={wind} className="img" alt="sunrise" />
                                    </div>
                                    <h3>{data.windSpeed}</h3>
                                </div>
                                <div className="box">
                                    <h3>Feels Like</h3>
                                    <div className="border">
                                        <img src={`https://openweathermap.org/img/wn/${data.icon}@2x.png`} className="icon-img" alt="" />
                                    </div>
                                    <h3>{data.desc}</h3>
                                </div>
                                <div className="box rise">
                                    <h3>Sunrise</h3>
                                    <div className="border">
                                        <img src={sunrise} className="img" alt="sunrise" />
                                    </div>
                                    <h3>{moment(data.sunrise * 1000).format("hh:mm a")}</h3>
                                </div>
                                <div className="box">
                                    <h3>Sunset</h3>
                                    <div className="border">
                                        <img src={sunset} className="img" alt="sunset" />
                                    </div>
                                    <h3>{moment(data.sunset * 1000).format("hh:mm a")}</h3>
                                </div>
                                <div className="box">
                                    <h3>Pressure</h3>
                                    <div className="border">
                                        <img src={pressure} className="img" alt="pressure" />
                                    </div>
                                    <h3>{data.pressure}</h3>
                                </div>
                                <div className="box">
                                    <h3>Humidity</h3>
                                    <div className="border">
                                        <img src={humidity} className="img" alt="humidity" />
                                    </div>
                                    <h3>{data.humidity}</h3>
                                </div>
                            </div>
                        </>
                    )}
                </div>
            </div>
        </div>
    );
}

export default Card;
