import React, { useState, useEffect } from 'react';
import axios from 'axios';
import moment from 'moment';

import sunshineBackground from '../assets/sunny.gif';
import cloudyBackground from '../assets/cloudy.gif';
import rainyBackground from '../assets/thunder.gif';
import snowyBackground from '../assets/snoww.gif';
import defaultBackground from '../assets/IMG.jpg';

import sunset from '../assets/sunset.gif';
import sunrise from '../assets/sunrise.gif';
import hot from '../assets/hot.gif';
import cold from '../assets/cold.gif';
import cloudyIcon from '../assets/cloudy.gif';
import humidity from '../assets/humidity.png';
import pressure from '../assets/pressure.png';
import wind from '../assets/wind.gif';

function Card() {
    const [defaultCity, setDefaultCity] = useState('Nellore');
    const [timeGap, setTimeGap] = useState("");
    const [data, setData] = useState({
        city: "",
        temp: 0,
        temp_max: 0,
        temp_min: 0,
        humidity: 0,
        sunrise: 0,
        sunset: 0,
        country: "",
    });
    const [backgroundImage, setBackgroundImage] = useState(defaultBackground);

    const getData = (city) => {
        axios
            .get(`https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=d8596b1261b43be39522177d29112a96`)
            .then((response) => {
                const weatherData = response.data;

                setData({
                    desc: weatherData.weather[0].main,
                    icon: weatherData.weather[0].icon,
                    city: weatherData.name,
                    temp: weatherData.main.temp,
                    temp_max: weatherData.main.temp_max,
                    temp_min: weatherData.main.temp_min,
                    humidity: weatherData.main.humidity,
                    pressure: weatherData.main.pressure,
                    sunrise: weatherData.sys.sunrise,
                    sunset: weatherData.sys.sunset,
                    country: weatherData.sys.country,
                    windSpeed: weatherData.wind.speed,
                });
            })
            .catch((error) => {
                console.error('Error fetching weather data:', error);
            });
    };

    const handleOnChange = (e) => {
        clearTimeout(timeGap);
        const timeout = setTimeout(() => {
            getData(e.target.value);
        }, 500);
        setTimeGap(timeout);
    };

    useEffect(() => {
        if (data.desc) {
            let weatherCondition = data.desc.toLowerCase();
            if (weatherCondition.includes('clear')) {
                setBackgroundImage(sunshineBackground);
            } else if (weatherCondition.includes('cloud')) {
                setBackgroundImage(cloudyBackground);
            } else if (weatherCondition.includes('rain') || weatherCondition.includes('drizzle')) {
                setBackgroundImage(rainyBackground);
            } else if (weatherCondition.includes('snow')) {
                setBackgroundImage(snowyBackground);
            } else {
                setBackgroundImage(defaultBackground);
            }
        }
    }, [data.desc]);

    return (
        <div style={{ backgroundImage: `url(${backgroundImage})`, backgroundSize: 'cover', height: '100vh' }}>
            <div className="area">
                <ul className="circles">
                    {[...Array(10)].map((_, index) => (
                        <li key={index}></li>
                    ))}
                </ul>
            </div>
            <div className="container">
                <div className="card">
                    <div className="inputData">
                        <input
                            type="text"
                            className='inputField'
                            placeholder='Enter City Name'
                            onChange={handleOnChange}
                        />
                    </div>
                    <div className="city">
                        <h3><i className="fa-sharp fa-solid fa-location-dot"></i> {data.city}  {data.country}</h3>
                    </div>
                    <div className="info">
                        <h2>{data.temp} °C</h2>
                    </div>
                    <div className="boxes">
                        <div className="box">
                            <h4>Max Temp</h4>
                            <div className="border">
                                <img src={hot} className="img" alt="max_temp" />
                            </div>
                            <h3>{data.temp_max} °C</h3>
                        </div>
                        <div className="box">
                            <h4>Min Temp</h4>
                            <div className="border">
                                <img src={cold} className="img" alt="min_temp" />
                            </div>
                            <h3>{data.temp_min} °C</h3>
                        </div>
                        <div className="box">
                            <h3>Wind Speed</h3>
                            <div className="border">
                                <img src={wind} className="img" alt="wind_speed" />
                            </div>
                            <h3>{data.windSpeed}</h3>
                        </div>
                        <div className="box">
                            <h3>Feels Like</h3>
                            <div className="border">
                                <img src={`https://openweathermap.org/img/wn/${data.icon}@2x.png`} className="icon-img" alt="weather_icon" />
                            </div>
                            <h3>{data.desc}</h3>
                        </div>
                        <div className="box rise">
                            <h3>Sunrise</h3>
                            <div className="border">
                                <img src={sunrise} className="img" alt="sunrise" />
                            </div>
                            <h3>{moment(data.sunrise * 1000).format("hh:mm a")}</h3>
                        </div>
                        <div className="box">
                            <h3>Sunset</h3>
                            <div className="border">
                                <img src={sunset} className="img" alt="sunset" />
                            </div>
                            <h3>{moment(data.sunset * 1000).format("hh:mm a")}</h3>
                        </div>
                        <div className="box">
                            <h3>Pressure</h3>
                            <div className="border">
                                <img src={pressure} className="img" alt="pressure" />
                            </div>
                            <h3>{data.pressure}</h3>
                        </div>
                        <div className="box">
                            <h3>Humidity</h3>
                            <div className="border">
                                <img src={humidity} className="img" alt="humidity" />
                            </div>
                            <h3>{data.humidity}</h3>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Card;
